// Service Worker for ChessHub Academy PWA
const CACHE_NAME = 'chesshub-v1';
const ASSETS_TO_CACHE = [
    '/',
    '/index.html',
    '/manifest.json',
    '/icon-192.png',
    '/icon-512.png'
];

// Install event - cache assets
self.addEventListener('install', (event) => {
    console.log('[Service Worker] Installing...');
    event.waitUntil(
        caches.open(CACHE_NAME)
            .then((cache) => {
                console.log('[Service Worker] Caching assets');
                return cache.addAll(ASSETS_TO_CACHE);
            })
            .then(() => self.skipWaiting())
    );
});

// Activate event - clean up old caches
self.addEventListener('activate', (event) => {
    console.log('[Service Worker] Activating...');
    event.waitUntil(
        caches.keys().then((cacheNames) => {
            return Promise.all(
                cacheNames.map((cacheName) => {
                    if (cacheName !== CACHE_NAME) {
                        console.log('[Service Worker] Deleting old cache:', cacheName);
                        return caches.delete(cacheName);
                    }
                })
            );
        }).then(() => self.clients.claim())
    );
});

// Fetch event - serve from cache, fallback to network
self.addEventListener('fetch', (event) => {
    event.respondWith(
        caches.match(event.request)
            .then((response) => {
                // Return cached version or fetch from network
                return response || fetch(event.request).then((fetchResponse) => {
                    // Cache successful responses
                    if (fetchResponse.ok) {
                        return caches.open(CACHE_NAME).then((cache) => {
                            cache.put(event.request, fetchResponse.clone());
                            return fetchResponse;
                        });
                    }
                    return fetchResponse;
                });
            })
            .catch(() => {
                // Return offline page if available
                if (event.request.mode === 'navigate') {
                    return caches.match('/');
                }
            })
    );
});

// Background sync for offline actions
self.addEventListener('sync', (event) => {
    if (event.tag === 'sync-stats') {
        event.waitUntil(syncUserStats());
    }
});

async function syncUserStats() {
    try {
        // Sync localStorage data to backend when online
        const stats = {
            xp: localStorage.getItem('userXP'),
            level: localStorage.getItem('userLevel'),
            puzzleStreak: localStorage.getItem('puzzleStreak'),
            totalPuzzlesSolved: localStorage.getItem('totalPuzzlesSolved')
        };

        // Send to backend API
        // await fetch('/api/sync-stats', {
        //     method: 'POST',
        //     body: JSON.stringify(stats),
        //     headers: { 'Content-Type': 'application/json' }
        // });

        console.log('[Service Worker] Stats synced successfully');
    } catch (error) {
        console.error('[Service Worker] Sync failed:', error);
    }
}

// Push notifications
self.addEventListener('push', (event) => {
    const data = event.data ? event.data.json() : {};
    const title = data.title || 'ChessHub Academy';
    const options = {
        body: data.body || 'New notification from ChessHub',
        icon: '/icon-192.png',
        badge: '/icon-192.png',
        data: data.url || '/'
    };

    event.waitUntil(
        self.registration.showNotification(title, options)
    );
});

//Notification click handler
self.addEventListener('notificationclick', (event) => {
    event.notification.close();
    event.waitUntil(
        clients.openWindow(event.notification.data || '/')
    );
});

import { useState, useEffect, useRef } from 'react';
import './AnimatedStats.css';

export default function AnimatedStats() {
    const [isVisible, setIsVisible] = useState(false);
    const [counts, setCounts] = useState({
        students: 0,
        coaches: 0,
        hours: 0,
        rating: 0
    });

    const sectionRef = useRef(null);

    const finalValues = {
        students: 500,
        coaches: 50,
        hours: 10000,
        rating: 98
    };

    useEffect(() => {
        const observer = new IntersectionObserver(
            ([entry]) => {
                if (entry.isIntersecting && !isVisible) {
                    setIsVisible(true);
                }
            },
            { threshold: 0.3 }
        );

        if (sectionRef.current) {
            observer.observe(sectionRef.current);
        }

        return () => {
            if (sectionRef.current) {
                observer.unobserve(sectionRef.current);
            }
        };
    }, [isVisible]);

    useEffect(() => {
        if (!isVisible) return;

        const duration = 2000; // 2 seconds
        const steps = 60;
        const stepDuration = duration / steps;

        let currentStep = 0;

        const interval = setInterval(() => {
            currentStep++;
            const progress = currentStep / steps;
            const easeOutQuad = 1 - Math.pow(1 - progress, 3);

            setCounts({
                students: Math.floor(finalValues.students * easeOutQuad),
                coaches: Math.floor(finalValues.coaches * easeOutQuad),
                hours: Math.floor(finalValues.hours * easeOutQuad),
                rating: Math.floor(finalValues.rating * easeOutQuad)
            });

            if (currentStep >= steps) {
                clearInterval(interval);
                setCounts(finalValues);
            }
        }, stepDuration);

        return () => clearInterval(interval);
    }, [isVisible]);

    return (
        <section ref={sectionRef} className="section stats-section">
            <div className="container">
                <div className="stats-grid">
                    <div className="stat-card glass-card">
                        <div className="stat-icon">≡ƒæ¿ΓÇì≡ƒÄô</div>
                        <div className="stat-content">
                            <div className="stat-number">
                                {counts.students.toLocaleString()}+
                            </div>
                            <div className="stat-label">Happy Students</div>
                            <div className="stat-description">
                                Learning and growing with us
                            </div>
                        </div>
                    </div>

                    <div className="stat-card glass-card">
                        <div className="stat-icon">≡ƒæ¿ΓÇì≡ƒÅ½</div>
                        <div className="stat-content">
                            <div className="stat-number">
                                {counts.coaches}+
                            </div>
                            <div className="stat-label">Expert Coaches</div>
                            <div className="stat-description">
                                FIDE-rated professionals
                            </div>
                        </div>
                    </div>

                    <div className="stat-card glass-card">
                        <div className="stat-icon">ΓÅ▒∩╕Å</div>
                        <div className="stat-content">
                            <div className="stat-number">
                                {counts.hours.toLocaleString()}+
                            </div>
                            <div className="stat-label">Training Hours</div>
                            <div className="stat-description">
                                Of quality chess education
                            </div>
                        </div>
                    </div>

                    <div className="stat-card glass-card">
                        <div className="stat-icon">Γ¡É</div>
                        <div className="stat-content">
                            <div className="stat-number">
                                {counts.rating}%
                            </div>
                            <div className="stat-label">Success Rate</div>
                            <div className="stat-description">
                                Students achieving their goals
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        </section>
    );
}

import { useState } from 'react';
import ChessBoard from '../ChessBoard/ChessBoard';
import { saveDemoAssessment } from '../../services/supabase';
import './DemoAssessment.css';

export default function DemoAssessment() {
    const [step, setStep] = useState('info'); // info, playing, analyzing, results
    const [formData, setFormData] = useState({
        name: '',
        email: '',
        phone: '',
        age: '',
        experience: 'beginner'
    });
    const [gameData, setGameData] = useState(null);
    const [assessment, setAssessment] = useState(null);

    function handleInputChange(e) {
        setFormData({
            ...formData,
            [e.target.name]: e.target.value
        });
    }

    function startAssessment() {
        if (!formData.name || !formData.email || !formData.phone) {
            alert('Please fill all required fields!');
            return;
        }
        setStep('playing');
    }

    function handleGameEnd(gameState) {
        setGameData(gameState);
        setStep('analyzing');
        analyzePerformance(gameState);
    }

    async function analyzePerformance(gameState) {
        // Simulate AI analysis
        await new Promise(resolve => setTimeout(resolve, 3000));

        const mockAssessment = {
            accuracy: Math.floor(Math.random() * 30) + 60,
            tacticalRating: Math.floor(Math.random() * 400) + 800,
            positionalRating: Math.floor(Math.random() * 400) + 800,
            recommendedCourse: formData.experience === 'beginner' ? 'Beginner (Level 1)' :
                formData.experience === 'intermediate' ? 'Intermediate (Level 2)' :
                    'Advanced (Level 3)',
            strengths: [
                'Good opening knowledge',
                'Defensive play',
                'Time management'
            ],
            weaknesses: [
                'Tactical awareness',
                'Endgame technique',
                'Calculation depth'
            ],
            suggestedFocus: [
                'Solving tactical puzzles daily',
                'Studying endgame fundamentals',
                'Analyzing master games'
            ]
        };

        setAssessment(mockAssessment);
        setStep('results');

        // In production: Save to Supabase
        await saveToSupabase({
            ...formData,
            gameData,
            assessment: mockAssessment,
            timestamp: new Date().toISOString()
        });
    }

    async function saveToSupabase(data) {
        try {
            const result = await saveDemoAssessment(data);

            if (result.success) {
                console.log('Γ£à Demo assessment saved to Supabase!', result.data);
            } else {
                console.error('Γ¥î Failed to save to Supabase:', result.error);
                // Still continue even if save fails - user sees results
            }

            return result;
        } catch (error) {
            console.error('Error in saveToSupabase:', error);
            return { success: false, error };
        }
    }

    return (
        <div className="demo-assessment">
            {step === 'info' && (
                <div className="assessment-form">
                    <div className="form-header">
                        <h2>≡ƒôï Book Free Demo Assessment</h2>
                        <p>Play a game and get personalized course recommendations from our AI</p>
                    </div>

                    <form onSubmit={(e) => { e.preventDefault(); startAssessment(); }}>
                        <div className="form-group">
                            <label>Name *</label>
                            <input
                                type="text"
                                name="name"
                                value={formData.name}
                                onChange={handleInputChange}
                                required
                            />
                        </div>

                        <div className="form-group">
                            <label>Email *</label>
                            <input
                                type="email"
                                name="email"
                                value={formData.email}
                                onChange={handleInputChange}
                                required
                            />
                        </div>

                        <div className="form-group">
                            <label>Phone *</label>
                            <input
                                type="tel"
                                name="phone"
                                value={formData.phone}
                                onChange={handleInputChange}
                                required
                            />
                        </div>

                        <div className="form-group">
                            <label>Age</label>
                            <input
                                type="number"
                                name="age"
                                value={formData.age}
                                onChange={handleInputChange}
                                min="5"
                                max="100"
                            />
                        </div>

                        <div className="form-group">
                            <label>Chess Experience</label>
                            <select
                                name="experience"
                                value={formData.experience}
                                onChange={handleInputChange}
                            >
                                <option value="beginner">Beginner (Just starting)</option>
                                <option value="intermediate">Intermediate (1-3 years)</option>
                                <option value="advanced">Advanced (3+ years)</option>
                            </select>
                        </div>

                        <button type="submit" className="btn-start-assessment">
                            Start Assessment Game ≡ƒÄ«
                        </button>
                    </form>
                </div>
            )}

            {step === 'playing' && (
                <div className="assessment-game">
                    <div className="game-instructions">
                        <h3>≡ƒÄ« Play Your Best Game!</h3>
                        <p>Play against yourself or with a friend. We'll analyze your moves and provide recommendations.</p>
                    </div>
                    <ChessBoard
                        onGameEnd={handleGameEnd}
                        interactive={true}
                    />
                </div>
            )}

            {step === 'analyzing' && (
                <div className="assessment-analyzing">
                    <div className="analyzing-animation">
                        <div className="spinner-large"></div>
                        <h3>≡ƒñû AI is Analyzing Your Game...</h3>
                        <p>Evaluating moves, patterns, and skill level</p>
                    </div>
                </div>
            )}

            {step === 'results' && assessment && (
                <div className="assessment-results">
                    <div className="results-header">
                        <h2>Γ£¿ Your Chess Assessment Results</h2>
                        <p>Based on AI analysis of your game</p>
                    </div>

                    <div className="results-grid">
                        <div className="result-card overall">
                            <h3>Overall Accuracy</h3>
                            <div className="big-number">{assessment.accuracy}%</div>
                            <p>Computer-analyzed accuracy</p>
                        </div>

                        <div className="result-card tactical">
                            <h3>Tactical Rating</h3>
                            <div className="big-number">{assessment.tacticalRating}</div>
                            <p>Estimated puzzle rating</p>
                        </div>

                        <div className="result-card positional">
                            <h3>Positional Rating</h3>
                            <div className="big-number">{assessment.positionalRating}</div>
                            <p>Strategic understanding</p>
                        </div>
                    </div>

                    <div className="recommendation-box">
                        <h3>≡ƒôÜ Recommended Course</h3>
                        <div className="course-recommendation">
                            {assessment.recommendedCourse}
                        </div>
                        <p>Based on your current skill level and performance</p>
                    </div>

                    <div className="analysis-details">
                        <div className="strengths">
                            <h4>≡ƒÆ¬ Strengths</h4>
                            <ul>
                                {assessment.strengths.map((strength, idx) => (
                                    <li key={idx}>Γ£ô {strength}</li>
                                ))}
                            </ul>
                        </div>

                        <div className="weaknesses">
                            <h4>≡ƒÄ» Areas for Improvement</h4>
                            <ul>
                                {assessment.weaknesses.map((weakness, idx) => (
                                    <li key={idx}>ΓåÆ {weakness}</li>
                                ))}
                            </ul>
                        </div>

                        <div className="focus">
                            <h4>≡ƒôî Suggested Focus</h4>
                            <ul>
                                {assessment.suggestedFocus.map((focus, idx) => (
                                    <li key={idx}>ΓÇó {focus}</li>
                                ))}
                            </ul>
                        </div>
                    </div>

                    <div className="next-steps">
                        <h3>≡ƒÜÇ Next Steps</h3>
                        <p>Our team will contact you within 24 hours to schedule your free demo class!</p>
                        <button className="btn-finish" onClick={() => setStep('info')}>
                            Take Another Assessment
                        </button>
                    </div>
                </div>
            )}
        </div>
    );
}

import { useState, useCallback } from 'react';
import { Chessboard } from 'react-chessboard';
import useChessGame from '../../hooks/useChessGame';
import './ChessBoard.css';

export default function ChessBoard({
    initialPosition = 'start',
    boardOrientation = 'white',
    interactive = true,
    showNotation = true,
    onGameEnd,
    customStyles = {}
}) {
    const {
        position,
        moveHistory,
        isGameOver,
        result,
        makeMove,
        getLegalMoves,
        undoMove,
        resetGame,
        getGameState
    } = useChessGame(initialPosition);

    const [selectedSquare, setSelectedSquare] = useState(null);
    const [legalMoves, setLegalMoves] = useState([]);
    const [moveFrom, setMoveFrom] = useState('');
    const [moveTo, setMoveTo] = useState(null);
    const [optionSquares, setOptionSquares] = useState({});

    /**
     * Handle piece drop on board
     */
    function onDrop(sourceSquare, targetSquare) {
        if (!interactive) return false;

        const move = makeMove({
            from: sourceSquare,
            to: targetSquare,
            promotion: 'q' // Always promote to queen for now
        });

        // If move was successful
        if (move) {
            setSelectedSquare(null);
            setLegalMoves([]);
            setOptionSquares({});

            // Check if game ended
            if (isGameOver && onGameEnd) {
                const gameState = getGameState();
                onGameEnd(gameState);
            }

            return true;
        }

        return false;
    }

    /**
     * Handle square click for move selection
     */
    function onSquareClick(square) {
        if (!interactive) return;

        // If no square selected, select this square
        if (!selectedSquare) {
            const moves = getLegalMoves(square);
            if (moves.length > 0) {
                setSelectedSquare(square);
                setLegalMoves(moves);

                // Highlight legal move squares
                const squares = {};
                moves.forEach(move => {
                    squares[move] = {
                        background: 'radial-gradient(circle, rgba(139, 92, 246, 0.5) 25%, transparent 25%)',
                        borderRadius: '50%'
                    };
                });
                setOptionSquares(squares);
            }
        }
        // If square already selected
        else {
            // Try to make move to clicked square
            const move = makeMove({
                from: selectedSquare,
                to: square,
                promotion: 'q'
            });

            if (move) {
                setSelectedSquare(null);
                setLegalMoves([]);
                setOptionSquares({});

                if (isGameOver && onGameEnd) {
                    const gameState = getGameState();
                    onGameEnd(gameState);
                }
            } else {
                // Check if clicking on own piece to select it
                const moves = getLegalMoves(square);
                if (moves.length > 0) {
                    setSelectedSquare(square);
                    setLegalMoves(moves);

                    const squares = {};
                    moves.forEach(move => {
                        squares[move] = {
                            background: 'radial-gradient(circle, rgba(139, 92, 246, 0.5) 25%, transparent 25%)',
                            borderRadius: '50%'
                        };
                    });
                    setOptionSquares(squares);
                } else {
                    setSelectedSquare(null);
                    setLegalMoves([]);
                    setOptionSquares({});
                }
            }
        }
    }

    /**
     * Handle right-click for drawing arrows (future feature)
     */
    function onSquareRightClick(square) {
        // Future: Drawing arrows and circles
        console.log('Right click on:', square);
    }

    return (
        <div className="chess-board-container">
            <div className="chess-board-wrapper">
                <Chessboard
                    position={position}
                    onPieceDrop={onDrop}
                    onSquareClick={onSquareClick}
                    onSquareRightClick={onSquareRightClick}
                    boardOrientation={boardOrientation}
                    customBoardStyle={{
                        borderRadius: '8px',
                        boxShadow: '0 8px 32px rgba(0, 0, 0, 0.3)',
                        ...customStyles
                    }}
                    customSquareStyles={{
                        ...optionSquares,
                        ...(selectedSquare && {
                            [selectedSquare]: {
                                background: 'rgba(255, 255, 0, 0.4)'
                            }
                        })
                    }}
                    arePiecesDraggable={interactive}
                    showBoardNotation={showNotation}
                />
            </div>

            <div className="chess-controls">
                <button
                    className="chess-btn chess-btn-undo"
                    onClick={undoMove}
                    disabled={moveHistory.length === 0}
                >
                    Γå╢ Undo
                </button>

                <button
                    className="chess-btn chess-btn-reset"
                    onClick={resetGame}
                >
                    Γƒ▓ Reset
                </button>

                {isGameOver && result && (
                    <div className="game-result">
                        <div className="result-badge">{result}</div>
                    </div>
                )}
            </div>

            <div className="move-history">
                <h4>Moves</h4>
                <div className="moves-list">
                    {moveHistory.map((move, index) => (
                        <span key={index} className="move-item">
                            {index % 2 === 0 && <span className="move-number">{Math.floor(index / 2) + 1}.</span>}
                            <span className="move-notation">{move.san}</span>
                        </span>
                    ))}
                </div>
            </div>
        </div>
    );
}

