        // Get all users' puzzle stats from localStorage
        // In production, this would be from a backend API
        const mockData = [
            { rank: 1, name: 'ChessMaster', score: 1250, streak: 45, avatar: '≡ƒææ' },
            { rank: 2, name: 'TacticalGenius', score: 1180, streak: 32, avatar: '≡ƒöÑ' },
            { rank: 3, name: 'PuzzleKing', score: 1050, streak: 28, avatar: 'Γ¡É' },
            { rank: 4, name: 'StrategicMind', score: 980, streak: 21, avatar: '≡ƒÄ»' },
            { rank: 5, name: 'CheckmateQueen', score: 925, streak: 19, avatar: '≡ƒæ╕' },
            { rank: 6, name: 'EndgameExpert', score: 890, streak: 17, avatar: '≡ƒÅå' },
            { rank: 7, name: 'Opening Guru', score: 845, streak: 15, avatar: '≡ƒôÜ' },
            { rank: 8, name: 'BlitzMaster', score: 800, streak: 12, avatar: 'ΓÜí' },
            { rank: 9, name: 'PatientPlayer', score: 775, streak: 10, avatar: '≡ƒºÿ' },
            { rank: 10, name: 'StudyHero', score: 750, streak: 8, avatar: '≡ƒôû' }
        ];

        setLeaderboard(mockData);

        // Calculate user's rank based on their XP
        const userScore = parseInt(localStorage.getItem('userXP') || '0');
        const userStreakVal = parseInt(localStorage.getItem('puzzleStreak') || '0');

        if (userScore > 0) {
            const rank = mockData.filter(u => u.score > userScore).length + 1;
            setUserRank({
                rank,
