import { useState, useEffect } from 'react';
import './PuzzleStreakTracker.css';

export default function PuzzleStreakTracker() {
    const [streak, setStreak] = useState(0);
    const [bestStreak, setBestStreak] = useState(0);
    const [totalSolved, setTotalSolved] = useState(0);

    useEffect(() => {
        loadStats();
    }, []);

    function loadStats() {
        const currentStreak = parseInt(localStorage.getItem('puzzleStreak') || '0');
        const best = parseInt(localStorage.getItem('bestPuzzleStreak') || '0');
        const total = parseInt(localStorage.getItem('totalPuzzlesSolved') || '0');

        setStreak(currentStreak);
        setBestStreak(Math.max(currentStreak, best));
        setTotalSolved(total);

        // Update best streak if current is higher
        if (currentStreak > best) {
            localStorage.setItem('bestPuzzleStreak', currentStreak.toString());
        }
    }

    // Update stats when local storage changes
    useEffect(() => {
        const handleStorageChange = () => {
            loadStats();
        };

        window.addEventListener('storage', handleStorageChange);
        // Also check periodically
        const interval = setInterval(loadStats, 2000);

        return () => {
            window.removeEventListener('storage', handleStorageChange);
            clearInterval(interval);
        };
    }, []);

    function getStreakEmoji() {
        if (streak === 0) return '≡ƒÄ»';
        if (streak < 3) return '≡ƒöÑ';
        if (streak < 7) return '≡ƒöÑ≡ƒöÑ';
        if (streak < 30) return '≡ƒöÑ≡ƒöÑ≡ƒöÑ';
        return '≡ƒöÑ≡ƒöÑ≡ƒöÑ≡ƒöÑ';
    }

    function getStreakMessage() {
        if (streak === 0) return 'Start your streak!';
        if (streak === 1) return 'Great start!';
        if (streak < 7) return 'On fire!';
        if (streak < 30) return 'Unstoppable!';
        return 'Legendary streak!';
    }

    return (
        <div className="streak-tracker">
            <div className="streak-header">
                <h3>≡ƒÄ» Puzzle Streak</h3>
            </div>

            <div className="streak-stats">
                <div className="stat-card streak-current">
                    <div className="stat-icon">{getStreakEmoji()}</div>
                    <div className="stat-value">{streak}</div>
                    <div className="stat-label">Current Streak</div>
                    <div className="stat-message">{getStreakMessage()}</div>
                </div>

                <div className="stat-card streak-best">
                    <div className="stat-icon">≡ƒÅå</div>
                    <div className="stat-value">{bestStreak}</div>
                    <div className="stat-label">Best Streak</div>
                </div>

                <div className="stat-card streak-total">
                    <div className="stat-icon">Γ£à</div>
                    <div className="stat-value">{totalSolved}</div>
                    <div className="stat-label">Total Solved</div>
                </div>
            </div>

            {streak >= 3 && (
                <div className="streak-milestone">
                    <div className="milestone-badge">
                        <span className="badge-icon">Γ¡É</span>
                        <span className="badge-text">
                            {streak >= 30 ? '30-Day Champion!' :
                                streak >= 7 ? '7-Day Warrior!' :
                                    '3-Day Achiever!'}
                        </span>
                    </div>
                </div>
            )}

            <div className="streak-progress">
                <div className="progress-label">
                    <span>Next Milestone:</span>
                    <span className="progress-target">
                        {streak < 3 ? '3 Days' :
                            streak < 7 ? '7 Days' :
                                streak < 30 ? '30 Days' :
                                    '100 Days'}
                    </span>
                </div>
                <div className="progress-bar">
                    <div
                        className="progress-fill"
                        style={{
                            width: `${streak < 3 ? (streak / 3) * 100 :
                                    streak < 7 ? ((streak - 3) / 4) * 100 :
                                        streak < 30 ? ((streak - 7) / 23) * 100 :
                                            ((streak - 30) / 70) * 100
                                }%`
                        }}
                    ></div>
                </div>
            </div>
        </div>
    );
}

import { useState, useEffect } from 'react';
import Joyride from 'react-joyride';
import './TutorialWalkthrough.css';

export default function TutorialWalkthrough({ run, onFinish }) {
    const [runTour, setRunTour] = useState(run);

    useEffect(() => {
        setRunTour(run);
    }, [run]);

    const steps = [
        {
            target: '.level-progress-container',
            content: '≡ƒÄ« Track your progress here! Earn XP by solving puzzles and completing challenges to level up.',
            disableBeacon: true,
        },
        {
            target: '.features-tabs',
            content: '≡ƒÄ» Explore different chess features using these tabs. Each tab offers unique ways to improve your game!',
        },
        {
            target: '.tab-btn:first-child',
            content: 'ΓÖƒ∩╕Å Practice on our interactive chess board with drag-and-drop pieces and legal move highlighting.',
        },
        {
            target: '.tab-btn:nth-child(2)',
            content: '≡ƒº⌐ Solve daily puzzles from Lichess to sharpen your tactical skills and earn XP!',
        },
        {
            target: '.tab-btn:nth-child(3)',
            content: '≡ƒöÑ Build streaks by solving puzzles consistently. Unlock milestone badges at 3, 7, and 30 days!',
        },
        {
            target: '.tab-btn:nth-child(4)',
            content: '≡ƒÄ» Complete 3 daily challenges to earn bonus XP and build your daily streak!',
        },
        {
            target: '.tab-btn:nth-child(5)',
            content: '≡ƒÅå Compete with other players on the leaderboard and climb the rankings!',
        },
        {
            target: '.font-size-control',
            content: '≡ƒöñ Adjust text size for better readability using these accessibility controls.',
        },
        {
            target: 'body',
            content: 'Γ£¿ You\'re all set! Start solving puzzles, complete challenges, and watch your level grow. Good luck! ≡ƒÜÇ',
            placement: 'center',
        },
    ];

    const handleJoyrideCallback = (data) => {
        const { status } = data;
        const finishedStatuses = ['finished', 'skipped'];

        if (finishedStatuses.includes(status)) {
            setRunTour(false);
            localStorage.setItem('tutorialCompleted', 'true');
            if (onFinish) onFinish();
        }
    };

    return (
        <Joyride
            steps={steps}
            run={runTour}
            continuous
            showProgress
            showSkipButton
            callback={handleJoyrideCallback}
            styles={{
                options: {
                    primaryColor: '#8b5cf6',
                    textColor: '#fff',
                    backgroundColor: '#1a1a2e',
                    overlayColor: 'rgba(0, 0, 0, 0.7)',
                    arrowColor: '#1a1a2e',
                    zIndex: 10000,
                },
                buttonNext: {
                    backgroundColor: '#8b5cf6',
                    borderRadius: '8px',
                    padding: '10px 20px',
                },
                buttonBack: {
                    color: '#8b5cf6',
                },
                buttonSkip: {
                    color: '#ef4444',
                },
                tooltip: {
                    borderRadius: '12px',
                    padding: '20px',
                },
            }}
            locale={{
                back: 'ΓåÉ Back',
                close: 'Close',
                last: 'Finish',
                next: 'Next ΓåÆ',
                skip: 'Skip Tour',
            }}
        />
    );
}

// Hook to manage tutorial state
export function useTutorial() {
    const [showTutorial, setShowTutorial] = useState(false);

    useEffect(() => {
        const completed = localStorage.getItem('tutorialCompleted');
        if (!completed) {
            // Show tutorial after a short delay
            setTimeout(() => setShowTutorial(true), 1000);
        }
    }, []);

    const startTutorial = () => setShowTutorial(true);
    const resetTutorial = () => {
        localStorage.removeItem('tutorialCompleted');
        setShowTutorial(true);
    };

    return {
        showTutorial,
        startTutorial,
        resetTutorial,
        finishTutorial: () => setShowTutorial(false),
    };
}

