import { useState, useEffect } from 'react';
import { LineChart, Line, BarChart, Bar, PieChart, Pie, Cell, XAxis, YAxis, CartesianGrid, Tooltip, Legend, ResponsiveContainer } from 'recharts';
import './AdminDashboard.css';

export default function AdminDashboard() {
    const [bookings, setBookings] = useState([]);
    const [stats, setStats] = useState({
        total: 0,
        today: 0,
        thisWeek: 0,
        thisMonth: 0,
        pending: 0,
        confirmed: 0,
    });

    useEffect(() => {
        loadData();
    }, []);

    const loadData = () => {
        const storedBookings = JSON.parse(localStorage.getItem('demoBookings') || '[]');
        setBookings(storedBookings);

        const now = new Date();
        const today = new Date(now.getFullYear(), now.getMonth(), now.getDate());
        const weekAgo = new Date(today.getTime() - 7 * 24 * 60 * 60 * 1000);
        const monthAgo = new Date(today.getTime() - 30 * 24 * 60 * 60 * 1000);

        const todayCount = storedBookings.filter(b => {
            const bookingDate = new Date(b.timestamp);
            return bookingDate >= today;
        }).length;

        const weekCount = storedBookings.filter(b => {
            const bookingDate = new Date(b.timestamp);
            return bookingDate >= weekAgo;
        }).length;

        const monthCount = storedBookings.filter(b => {
            const bookingDate = new Date(b.timestamp);
            return bookingDate >= monthAgo;
        }).length;

        setStats({
            total: storedBookings.length,
            today: todayCount,
            thisWeek: weekCount,
            thisMonth: monthCount,
            pending: storedBookings.filter(b => !b.status || b.status === 'pending').length,
            confirmed: storedBookings.filter(b => b.status === 'confirmed').length,
        });
    };

    // Chart data
    const bookingsOverTime = [
        { name: 'Mon', bookings: 4 },
        { name: 'Tue', bookings: 3 },
        { name: 'Wed', bookings: 7 },
        { name: 'Thu', bookings: 5 },
        { name: 'Fri', bookings: 8 },
        { name: 'Sat', bookings: 6 },
        { name: 'Sun', bookings: 2 },
    ];

    const statusData = [
        { name: 'Pending', value: stats.pending },
        { name: 'Confirmed', value: stats.confirmed },
    ];

    const COLORS = ['#8b5cf6', '#3b82f6', '#ec4899', '#06b6d4'];

    const recentBookings = bookings.slice(0, 5);

    return (
        <div className="admin-dashboard">
            <h2>Dashboard Overview</h2>

            {/* Stats Cards */}
            <div className="stats-grid">
                <div className="glass-card stat-card">
                    <div className="stat-icon">≡ƒôè</div>
                    <div className="stat-info">
                        <div className="stat-value">{stats.total}</div>
                        <div className="stat-label">Total Bookings</div>
                    </div>
                </div>

                <div className="glass-card stat-card">
                    <div className="stat-icon">≡ƒôà</div>
                    <div className="stat-info">
                        <div className="stat-value">{stats.today}</div>
                        <div className="stat-label">Today</div>
                    </div>
                </div>

                <div className="glass-card stat-card">
                    <div className="stat-icon">≡ƒôê</div>
                    <div className="stat-info">
                        <div className="stat-value">{stats.thisWeek}</div>
                        <div className="stat-label">This Week</div>
                    </div>
                </div>

                <div className="glass-card stat-card">
                    <div className="stat-icon">≡ƒÆ░</div>
                    <div className="stat-info">
                        <div className="stat-value">{stats.thisMonth}</div>
                        <div className="stat-label">This Month</div>
                    </div>
                </div>
            </div>

            {/* Charts */}
            <div className="charts-grid">
                <div className="glass-card chart-card">
                    <h3>Bookings This Week</h3>
                    <ResponsiveContainer width="100%" height={300}>
                        <LineChart data={bookingsOverTime}>
                            <CartesianGrid strokeDasharray="3 3" stroke="#333" />
                            <XAxis dataKey="name" stroke="#a1a1aa" />
                            <YAxis stroke="#a1a1aa" />
                            <Tooltip
                                contentStyle={{ background: '#1a1a24', border: '1px solid #333', borderRadius: '8px' }}
                            />
                            <Legend />
                            <Line type="monotone" dataKey="bookings" stroke="#8b5cf6" strokeWidth={2} />
                        </LineChart>
                    </ResponsiveContainer>
                </div>

                <div className="glass-card chart-card">
                    <h3>Booking Status</h3>
                    <ResponsiveContainer width="100%" height={300}>
                        <PieChart>
                            <Pie
                                data={statusData}
                                cx="50%"
                                cy="50%"
                                labelLine={false}
                                label={({ name, percent }) => `${name} ${(percent * 100).toFixed(0)}%`}
                                outerRadius={80}
                                fill="#8884d8"
                                dataKey="value"
                            >
                                {statusData.map((entry, index) => (
                                    <Cell key={`cell-${index}`} fill={COLORS[index % COLORS.length]} />
                                ))}
                            </Pie>
                            <Tooltip
                                contentStyle={{ background: '#1a1a24', border: '1px solid #333', borderRadius: '8px' }}
                            />
                        </PieChart>
                    </ResponsiveContainer>
                </div>
            </div>

            {/* Recent Activity */}
            <div className="glass-card recent-activity">
                <h3>Recent Bookings</h3>
                {recentBookings.length === 0 ? (
                    <p className="empty-state">No bookings yet</p>
                ) : (
                    <div className="activity-list">
                        {recentBookings.map((booking) => (
                            <div key={booking.id} className="activity-item">
                                <div className="activity-icon">≡ƒæñ</div>
                                <div className="activity-info">
                                    <div className="activity-title">{booking.name}</div>
                                    <div className="activity-details">
                                        {booking.email} ΓÇó {new Date(booking.timestamp).toLocaleDateString()}
                                    </div>
                                </div>
                                <div className="activity-status">
                                    <span className={`status-badge ${booking.status || 'pending'}`}>
                                        {booking.status || 'pending'}
                                    </span>
                                </div>
                            </div>
                        ))}
                    </div>
                )}
            </div>
        </div>
    );
}

import { useState } from 'react';
import './AdminSettings.css';

export default function AdminSettings() {
    const [settings, setSettings] = useState({
        siteName: 'ChessHub Academy',
        email: 'clubchess259@gmail.com',
        phone: '+91 7008665245',
        address: 'India',
        timezone: 'Asia/Kolkata',
        currency: 'INR',
        emailNotifications: true,
        smsNotifications: false,
        autoConfirm: false,
    });

    const [saved, setSaved] = useState(false);

    const handleChange = (field, value) => {
        setSettings(prev => ({ ...prev, [field]: value }));
    };

    const handleSave = () => {
        localStorage.setItem('siteSettings', JSON.stringify(settings));
        setSaved(true);
        setTimeout(() => setSaved(false), 3000);
    };

    const handleBackup = () => {
        const data = {
            bookings: JSON.parse(localStorage.getItem('demoBookings') || '[]'),
            settings: settings,
            timestamp: new Date().toISOString(),
        };

        const blob = new Blob([JSON.stringify(data, null, 2)], { type: 'application/json' });
        const url = window.URL.createObjectURL(blob);
        const a = document.createElement('a');
        a.href = url;
        a.download = `chesshub-backup-${new Date().toISOString().split('T')[0]}.json`;
        a.click();
    };

    const handleRestore = (e) => {
        const file = e.target.files[0];
        if (file) {
            const reader = new FileReader();
            reader.onload = (event) => {
                try {
                    const data = JSON.parse(event.target.result);
                    if (data.bookings) {
                        localStorage.setItem('demoBookings', JSON.stringify(data.bookings));
                    }
                    if (data.settings) {
                        setSettings(data.settings);
                        localStorage.setItem('siteSettings', JSON.stringify(data.settings));
                    }
                    alert('Backup restored successfully!');
                } catch (error) {
                    alert('Invalid backup file');
                }
            };
            reader.readAsText(file);
        }
    };

    return (
        <div className="admin-settings">
            <h2>Settings & Configuration</h2>

            {/* General Settings */}
            <div className="glass-card settings-section">
                <h3>General Settings</h3>
                <div className="settings-grid">
                    <div className="form-group">
                        <label className="form-label">Site Name</label>
                        <input
                            type="text"
                            value={settings.siteName}
                            onChange={(e) => handleChange('siteName', e.target.value)}
                            className="form-input"
                        />
                    </div>

                    <div className="form-group">
                        <label className="form-label">Email</label>
                        <input
                            type="email"
                            value={settings.email}
                            onChange={(e) => handleChange('email', e.target.value)}
                            className="form-input"
                        />
                    </div>

                    <div className="form-group">
                        <label className="form-label">Phone</label>
                        <input
                            type="tel"
                            value={settings.phone}
                            onChange={(e) => handleChange('phone', e.target.value)}
                            className="form-input"
                        />
                    </div>

                    <div className="form-group">
                        <label className="form-label">Address</label>
                        <input
                            type="text"
                            value={settings.address}
                            onChange={(e) => handleChange('address', e.target.value)}
                            className="form-input"
                        />
                    </div>

                    <div className="form-group">
                        <label className="form-label">Timezone</label>
                        <select
                            value={settings.timezone}
                            onChange={(e) => handleChange('timezone', e.target.value)}
                            className="form-input"
                        >
                            <option value="Asia/Kolkata">Asia/Kolkata (IST)</option>
                            <option value="America/New_York">America/New_York (EST)</option>
                            <option value="Europe/London">Europe/London (GMT)</option>
                        </select>
                    </div>

                    <div className="form-group">
                        <label className="form-label">Currency</label>
                        <select
                            value={settings.currency}
                            onChange={(e) => handleChange('currency', e.target.value)}
                            className="form-input"
                        >
                            <option value="INR">INR (Γé╣)</option>
                            <option value="USD">USD ($)</option>
                            <option value="EUR">EUR (Γé¼)</option>
                        </select>
                    </div>
                </div>
            </div>

            {/* Notification Settings */}
            <div className="glass-card settings-section">
                <h3>Notifications</h3>
                <div className="settings-list">
                    <div className="setting-item">
                        <div className="setting-info">
                            <div className="setting-title">Email Notifications</div>
                            <div className="setting-description">Receive email alerts for new bookings</div>
                        </div>
                        <label className="toggle">
                            <input
                                type="checkbox"
                                checked={settings.emailNotifications}
                                onChange={(e) => handleChange('emailNotifications', e.target.checked)}
                            />
                            <span className="toggle-slider"></span>
                        </label>
                    </div>

                    <div className="setting-item">
                        <div className="setting-info">
                            <div className="setting-title">SMS Notifications</div>
                            <div className="setting-description">Receive SMS alerts for new bookings</div>
                        </div>
                        <label className="toggle">
                            <input
                                type="checkbox"
                                checked={settings.smsNotifications}
                                onChange={(e) => handleChange('smsNotifications', e.target.checked)}
                            />
                            <span className="toggle-slider"></span>
                        </label>
                    </div>

                    <div className="setting-item">
                        <div className="setting-info">
                            <div className="setting-title">Auto-Confirm Bookings</div>
                            <div className="setting-description">Automatically confirm new demo bookings</div>
                        </div>
                        <label className="toggle">
                            <input
                                type="checkbox"
                                checked={settings.autoConfirm}
                                onChange={(e) => handleChange('autoConfirm', e.target.checked)}
                            />
                            <span className="toggle-slider"></span>
                        </label>
                    </div>
                </div>
            </div>

            {/* Backup & Restore */}
            <div className="glass-card settings-section">
                <h3>Backup & Restore</h3>
                <div className="backup-actions">
                    <button onClick={handleBackup} className="btn btn-primary">
                        ≡ƒôÑ Download Backup
                    </button>
                    <label className="btn btn-secondary">
                        ≡ƒôñ Restore Backup
                        <input
                            type="file"
                            accept=".json"
                            onChange={handleRestore}
                            style={{ display: 'none' }}
                        />
                    </label>
                </div>
                <p className="backup-note">
                    Backup includes all bookings and settings. Restore will overwrite current data.
                </p>
            </div>

            {/* Save Button */}
            <div className="settings-footer">
                <button onClick={handleSave} className="btn btn-primary btn-lg">
                    {saved ? 'Γ£ô Saved!' : 'Save Settings'}
                </button>
            </div>
        </div>
    );
}

